}

.search-engine__toggles button {
  color: #212529;
}

.footer {
    border-top: var(--spacer-1) solid #c20d19;
}

.footer__store {
    color: #c20d19;
}

.btn-primary {
    background-color: #40AA60;
    border-color: #40AA60;
}

.product__free-shipping-from {
    color: #212529;
}

.shipping-calculator__option>small {
    padding-top: 3px;
}

/*CABEÇALHO CARRINHO*/
.app__header {
    background:  #c20d19;
}

/*COR DE CONTATO CARRINHO*/
.app__contact span:not(:first-child) {
    color: #FFFFFF;
}

.app__contact {
    color: #FFFFFF;
}

/*COR TITULO CARRINHO*/
#storefront-app>div>h1 {
    color: #212529!important;
}

/*COR DE # NO CARRINHO*/
#storefront-app>div>h1:before {
    color: #212529!important;
}

/*COR TEXTO AMBIENTE SEGURO CARRINHO*/
.app__safe {
    color: #FFFFFF;
}

/*COR DO PREÇO*/
.prices__value {
    color: #212529;
}

/*PESO DA FONTE DO BOTÃO COMPRAR*/
.btn-lg {
    font-weight: 700;
}

.login__info-title {
    color: #212529!important;
}

.checkout__step--current {
    color: #212529!important;
}

/*TEXTO DE CUPOM DE DESCONTO NO CHECKOUT*/
.discount-applier__intro {
    color: #212529!important;
}

.summary__amount__row--total strong {
    color: #212529!important;
}

@media screen and (max-width: 720px) {
  .app__contact {
    color: #FFFFFF!importante;
}

  .search-engine__toggles button {
    color: #212529!important;
  }
}
