css:
/*DEFINDO FONTE*/
body{
  font-family: 'Poppins', sans-serif;
}

h1{
  color: #212529;
}

/*RETIRAR ESPAÇO BARRA TOPO*/


/*DIMINUIR BARRA TOPO*/
.top-bar__countdown {
  font-size: 14px;
  padding-top: 0px;
  padding-top: 10px;
}

.top-bar__nav {
  padding: 0;
}

/*TROCAR COR BACKGROUND CABEÇALHO*/
main .header {
  background-color: #f5f5f5;
}

/*MOSTRAR BOTÃO COMPRAR NA VITRINE*/
.product-card .product-card__buy {
  opacity: 1!important;
}

.header__search-btn {
  color: #c20d19;
}

.header__nav a {
   color: #C20D19;
}

.header__nav a:hover {
   background-color: #C20D19;
   color: #f5f5f5;
}

.header {
    border-bottom: 1px solid #c20d19;
}

/*COR BACKGROUND SUBMENU*/
.header__submenu {
    background: #f5f5f5;
}

/*COR SETA VITRINE*/
.glide__arrow {
    color: #c20d19;
}

/*COR TÍTULO DA PAGINA*/
.page-title__head {
    color: #212529;
}

/*COR TOTAL CARRINHO*/
.minicart__subtotal {
    color: #212529;
}

/*COR PARCELADO CARRINHO APP*/
.prices:not(.prices--big) .prices__installments {
    color: #212529;
}

.btn-outline-secondary {
    border-color: #212529;
    color: #212529;
}

.search-engine__toggles button {
    color: #212529;
}

.search-engine__toggles button {
  color: #212529;
}

.footer {
    border-top: var(--spacer-1) solid #c20d19;
}

.footer__store {
    color: #c20d19;
}

.btn-primary {
    background-color: #40AA60;
    border-color: #40AA60;
}

.product__free-shipping-from {
    color: #212529;
}

.shipping-calculator__option>small {
    padding-top: 3px;
}

/*COR DO PREÇO*/
.prices__value {
    color: #212529;
}

/*PESO DA FONTE DO BOTÃO COMPRAR*/
.btn-lg {
    font-weight: 700;
}

.login__info-title {
    color: #212529!important;
}

.checkout__step--current {
    color: #212529!important;
}

/*TEXTO DE CUPOM DE DESCONTO NO CHECKOUT*/
.discount-applier__intro {
    color: #212529!important;
}

.summary__amount__row--total strong {
    color: #212529!important;
}

@media screen and (max-width: 720px) {


  .search-engine__toggles button {
    color: #212529!important;
  }
}
